/**
 * Simple object check.
 * @param item
 * @returns {boolean}
 */
const isObject = function(item) {
  return (item && typeof item === 'object' && !Array.isArray(item));
}

/**
 * Deep merge two objects.
 * @param target
 * @param ...sources
 */
const mergeDeep = function (target, ...sources) {
  if (!sources.length) return target;
  const source = sources.shift();

  if (isObject(target) && isObject(source)) {
    for (const key in source) {
      if (isObject(source[key])) {
        if (!target[key]) Object.assign(target, { [key]: {} });
        mergeDeep(target[key], source[key]);
      } else {
        Object.assign(target, { [key]: source[key] });
      }
    }
  }

  return mergeDeep(target, ...sources);
}

export default mergeDeep

export default (event_type) => {
  let event
  if (typeof(Event) === 'function') {
    event = new Event(event_type)
  } else {
    event = document.createEvent('Event');
    event.initEvent(event_type, true, true);
  }

  return event
}

const addEventListener = function (type, callback) {
  if (!(type in this.listeners)) {
    this.listeners[type] = []
  }
  this.listeners[type].push(callback)
}
const removeEventListener = function (type, callback) {
  if (!(type in this.listeners)) return

  let stack = this.listeners[type]
  for (let i = 0, l = stack.length; i < l; i++) {
    if (stack[i] === callback) {
      stack.splice(i, 1)
      return
    }
  }
}
const dispatchEvent = function (event, data) {
  if (!(event.type in this.listeners)) return true

  let stack = this.listeners[event.type].slice()
  for (let i = 0, l = stack.length; i < l; i++) {
    stack[i].call(this, event, data)
  }

  return !event.defaultPrevented
}

export default {
  addEventListener,
  removeEventListener,
  dispatchEvent
}

export default function (message, className) {
  let el = document.createElement('div')
  el.className = className
  el.innerHTML = message

  return el
}

import generateElement from '../util/generateElement'

const clearMessages = function () {
  if (!(this._messageNode instanceof Element)) return

  while(this._messageNode.firstChild) {
    this._messageNode.removeChild(this._messageNode.firstChild)
  }
}

const removeMessage = function (message) {
  if (!(this._messageNode instanceof Element)) return

  Array.from(this._messageNode.children).forEach(child => {
    if (child.innerHTML === message) {
      this._messageNode.removeChild(child)
    }
  })
}

// Externally expects element, messageNode as instance
const addMessage = function (base) {
  return function (message, status) {
    let msg = generateElement(message, `${base} ${status}`)
    let messages = this._messageNode

    if (messages === null) {
      let _messages = generateElement('', `${base}s`)
      let anchor = this.options.messageAnchor || this.element
      if (!(anchor instanceof Element)) return

      _messages.appendChild(msg)
      switch (this.options.messagePOS) {
        case 'top':
          anchor.prepend(_messages)
          break;
        case 'bottom':
        default:
          anchor.appendChild(_messages)
      }

      this._messageNode = _messages
    } else {
      if (Array.from(this._messageNode.children)
        .every(m => m.innerHTML !== msg.innerHTML)) {
        this._messageNode.appendChild(msg)
      }
    }
  }
}

export default {
  clearMessages,
  removeMessage,
  addMessage
}

import debug from './util/debug'
import mergeDeep from './util/mergeDeep'
import createEvent from './util/createEvent'

import events from './lib/events'
import messaging from './lib/messaging'

const filterNullObj = (obj) => {
  return Object.keys(obj).reduce((newObj, key) => {
    const value = obj[key]

    if (value !== null &&
      typeof value !== 'undefined') {
      newObj[key] = value
    }

    return newObj
  }, {})
}
const isValidRule = (rule) => {
  return typeof rule !== 'undefined' && rule !== null
}
const toBoolean = (string, def = null) => {
  if (typeof string !== 'string' || string.length === 0) return def

  if (string.toLowerCase() === 'true') return true
  if (string.toLowerCase() === 'false') return false
  return !!string
}

const dispatchEvent = (input) => (event_name) => {
  input.dispatchEvent(createEvent(event_name));
}

const VPField = function (element, options, customRules, onValidate = {}) {
  if (!(element instanceof Element)) {
    throw new Error('[VPField] Field Element must be instance of Element.')
  }

  this.input = null
  this.element = element
  this.listeners = {}
  this.canValidate = true
  this.options = Object.assign({
    forceRules: false,
    rules: {
      required: null,
      min: null,
      max: null,
      minLength: null,
      maxLength: null,
      pattern: null
    },
    formatter: {
      pre: null,
      post: null
    },
    errorClass: '-isError',
    messageAnchor: null,
    messagePOS: 'bottom',
    showFieldErrors: false,
    dirtyOnBlur: toBoolean(element.getAttribute('vp-dirty'), false),
    validateOnBlur: toBoolean(element.getAttribute('vp-blur'), true),
    watch: toBoolean(element.getAttribute('vp-watch'), true)
  }, options)

  this._onValidation = mergeDeep({
    isValid: {
      message: null,
      cb: null
    },
    isInvalid: {
      message: null,
      cb: null
    }
  }, onValidate)
  this._customRules = customRules

  this._messageNode = null
  this._messages = []
  this._isValid = null
  this._dirty = false

  this.getInput()
  if (this.input instanceof Element) {
    if (this.options.watch === true) {
      if (['radio', 'checkbox'].includes(this.input.getAttribute('type'))) {
        this.input.addEventListener('change', () => {
          if (this.options.dirtyOnBlur === false) {
            this._dirty = true
          }

          if (this.canValidate === true && this._dirty === true) {
            const emit = this._isValid !== null

            let valid = this.isValid()
            if (emit) {
              this.dispatchEvent(createEvent('onValidate'), valid)
            }
          }
        })
      } else {
        this.input.addEventListener('input', () => {
          if (this.options.dirtyOnBlur === false) {
            this._dirty = true
          }

          if (this.canValidate === true && this._dirty === true) {
            const emit = this._isValid !== null

            let valid = this.isValid()
            if (emit) {
              this.dispatchEvent(createEvent('onValidate'), valid)
            }
          }
        })
      }
    }

    if (this.options.validateOnBlur) {
      this.input.addEventListener('blur', () => {
        this._dirty = true

        let valid = this.isValid()
        this.dispatchEvent(createEvent('onValidate'), valid)
      })
    }
  }
}

VPField.prototype.parseInput = function () {
  if (!(this.input instanceof Element)) {
    throw new Error('[VPField] Input must be an instance of Element')
  }

  const inputRules = filterNullObj({
    min: this.input.getAttribute('min'),
    minLength: this.input.getAttribute('minlength'),
    max: this.input.getAttribute('max'),
    maxLength: this.input.getAttribute('maxlength'),
    pattern: this.input.getAttribute('pattern'),
    required: this.input.getAttribute('required')
  })

  const rules = this.options.forceRules
    ? Object.assign({}, inputRules, this.options.rules)
    : Object.assign({}, this.options.rules, inputRules)

  return {
    value: this.input.value,
    checked: this.input.checked,
    type: this.input.getAttribute('type'),
    name: this.input.getAttribute('data-name') || this.input.getAttribute('name') || this.input.tagName,
    rules
  }
}

VPField.prototype.getInput = function () {
  debug('[VPField] Querying inputs')

  let input = this.element.getElementsByTagName('input')
  let select = this.element.getElementsByTagName('select')
  let textarea = this.element.getElementsByTagName('textarea')

  if (input.length > 0) debug('[VPField] Found input', input)
  if (select.length > 0) debug('[VPField] Found select', select)
  if (textarea.length > 0) debug('[VPField] Found textarea', textarea)

  this.input = [].concat(
    Array.from(input),
    Array.from(select),
    Array.from(textarea)
  )[0]

  // if (this.input instanceof Element) {
  // TODO: Clobber w/ VueJS, figure out how to support
  // this.input.setAttribute('__value', this.input.value)

  // Object.defineProperty(this.input, 'value', {
  //   configurable: true,
  //   enumerable: true,
  //   get: function () {
  //     return this.getAttribute('__value')
  //   },
  //   set: function (val) {
  //     this.setAttribute('__value', val)

  //     if (this.tagName.toLowerCase() === 'input' && ['radio', 'checkbox'].includes(this.getAttribute('type'))) {
  //       this.dispatchEvent(new Event('change'))
  //     } else {
  //       this.dispatchEvent(new Event('input'))
  //     }
  //   }
  // })
  // }
}

VPField.prototype.isValid = function () {
  this.canValidate = false
  if (typeof this.options.formatter.pre === 'function') {
    this.options.formatter.pre(this.input, dispatchEvent(this.input))
  }

  let attributes = this.parseInput()
  let {
    value,
    checked,
    message,
    action,
    type,
    name,
    rules
  } = attributes

  let errors = []
  if (typeof this._customRules === 'function') {
    errors.push(this._customRules(attributes, this.element, this.input))
  } else if (Array.isArray(this._customRules)) {
    errors = errors.concat(this._customRules.map(rule => {
      if (typeof rule === 'function') {
        return rule(attributes, this.element, this.input)
      }

      return true
    }))
  }

  if (isValidRule(rules.min)) {
    errors.push(+value >= +rules.min
      ? true
      : `${name} must be more than ${rules.min}.`)
  }
  if (isValidRule(rules.max)) {
    errors.push(+value <= +rules.max
      ? true
      : `${name} must be less than ${rules.max}.`)
  }
  if (isValidRule(rules.minLength)) {
    errors.push(value.length >= +rules.minLength
      ? true
      : `${name} must be ${rules.minLength} characters or more.`)
  }
  if (isValidRule(rules.maxLength)) {
    errors.push(value.length <= +rules.maxLength
      ? true
      : `${name} must be ${rules.maxLength} characters or less.`)
  }
  if (isValidRule(rules.pattern)) {
    errors.push((rules.pattern instanceof RegExp 
      ? rules.pattern.test(value)
      : new RegExp(rules.pattern).test(value))
      ? true
      : `${name} is incorrectly formatted.`)
  }

  switch (type) {
  case 'radio':
  case 'checkbox':
    // One should always be selected if required
    if (isValidRule(rules.required) && rules.required) {
      errors.push(checked ? true : `${name} is required.`)
    }
    break
  default:
    if (isValidRule(rules.required) && rules.required) {
      errors.push(value.length > 0 ? true : `${name} is required.`)
    }
  }

  this.clearMessages()
  this._isValid = errors.every(err => err === true)

  if (typeof this.options.formatter.pre === 'string') {
    this.addMessage(this.options.formatter.pre, '-isInfo')
  }

  if (this._isValid) {
    this.element.classList.remove(this.options.errorClass)

    if (typeof this._onValidation.isValid.cb === 'function') {
      this._onValidation.isValid.cb()
    }
    if (typeof this._onValidation.isValid.message === 'string') {
      this.addMessage(this._onValidation.isValid.message, '-isValid')
    }
  } else {
    this.element.classList.add(this.options.errorClass)

    if (typeof this._onValidation.isInvalid.cb === 'function') {
      this._onValidation.isInvalid.cb()
    }

    if (this.options.showFieldErrors) {
      errors.filter(err => typeof err === 'string').forEach(err => {
        this.addMessage(err, '-isError')
      })
    }

    if (typeof this._onValidation.isInvalid.message === 'string') {
      this.addMessage(this._onValidation.isInvalid.message, '-isError')
    }
  }

  if (typeof this.options.formatter.post === 'function') {
    this.options.formatter.post(this.input, dispatchEvent(this.input))
  }

  this.canValidate = true
  return this._isValid
}

// EventTarget
VPField.prototype.listeners = null
VPField.prototype.addEventListener = events.addEventListener
VPField.prototype.removeEventListener = events.removeEventListener
VPField.prototype.dispatchEvent = events.dispatchEvent

// DOM Messaging
VPField.prototype.clearMessages = messaging.clearMessages
VPField.prototype.removeMessage = messaging.removeMessage
VPField.prototype.addMessage = messaging.addMessage('VPMessage')

export default VPField

// JQuery $(element).is(':visible')
export default function (element) {
  if (element instanceof Element) {
    return !!(element.offsetWidth || element.offsetHeight || element.getClientRects().length)
  } else {
    return null
  }
}

import VPField from './Field'

import debug from './util/debug'
import mergeDeep from './util/mergeDeep'
import isElemVisible from './util/isElemVisible'
import createEvent from './util/createEvent'

import events from './lib/events'
import messaging from './lib/messaging'

// Options include
// ---------------
// showMessage: <false> - Fieldset level error shown
// noChildren: <false> - No children errors (field level)
// fieldClass: 'field' - Child 'Field' className
//

const VPFieldset = function (element, strategy, options, onValidate = {}) {
  if (!(element instanceof Element)) {
    throw new Error('[VPFieldset] Valid Element is required.')
  }

  if (typeof strategy !== 'function') {
    throw new Error('[VPFieldset] Validation strategy passed is invalid.')
  }

  this.strategy = strategy
  this.element = element
  this.listeners = {}
  this.options = Object.assign({
    validateVisible: true,
    fieldClass: 'VPField',
    errorClass: '-isError',
    messageAnchor: null,
    messagePOS: 'bottom',
    scrollAnchor: null,
    scrollTo: true,
    watch: true
  }, options)

  this._onValidation = mergeDeep({
    isValid: {
      message: null,
      cb: null
    },
    isInvalid: {
      message: null,
      cb: null
    }
  }, onValidate)
  this._fields = []
  this._isValid = null

  this._messageNode = null
  this._messages = []
}

VPFieldset.prototype.isValid = function () {
  const fieldSetStatus = this._fields.reduce((status, field, index) => {
    debug('[VPFieldset] Validating field', index)
    if (this.options.validateVisible) {
      if (isElemVisible(field.element)) {
        debug('[VPFieldset] Field is visible, continuing')
        status.push(field.isValid())
      } else {
        debug('[VPFieldset] Skipping hidden field', field)
      }
    } else {
      status.push(field.isValid())
    }

    return status
  }, [])

  this.clearMessages()
  this._isValid = this.strategy(fieldSetStatus)
  if (this._isValid) {
    this.element.classList.remove(this.options.errorClass)

    if (typeof this._onValidation.isValid.cb === 'function') {
      this._onValidation.isValid.cb()
    }
    if (typeof this._onValidation.isValid.message === 'string') {
      this.addMessage(this._onValidation.isValid.message, '-isValid')
    }
  } else {
    this.element.classList.add(this.options.errorClass)
    if (this.options.scrollTo === true) {
      if (this.options.scrollAnchor instanceof Element) {
        this.options.scrollAnchor.scrollIntoView()
      } else {
        // Scroll to the first error
        const firstElement = this._fields.filter(f => f._isValid === false)[0]
        if (firstElement.element instanceof Element && 
          typeof firstElement.element.scrollIntoView === 'function') {
          firstElement.element.scrollIntoView()
        }
      }
    }

    if (typeof this._onValidation.isInvalid.cb === 'function') {
      this._onValidation.isInvalid.cb()
    }
    if (typeof this._onValidation.isInvalid.message === 'string') {
      this.addMessage(this._onValidation.isInvalid.message, '-isError')
    }
  }

  return this._isValid
}

VPFieldset.prototype.removeField = function (field) {
  if (!(field instanceof VPField)) {
    throw new Error('[VPFieldset] Field must be an instanceof VPField')
  }

  const index = this._fields.indexOf(field)
  if (index !== -1) {
    this._fields = this._fields.splice(index, 1)
  }
}

VPFieldset.prototype.watchField = function (field) {
  if (!(field instanceof VPField)) {
    throw new Error('Field must be an instance of VPField')
  }

  // TODO: Optimize by tracking state and only revalidating
  // if internal state changes. Currently wasteful
  field.addEventListener('onValidate', (e, isValid) => {
    const valid = this.isValid()
    const emit = this._isValid !== null

    if (emit) {
      this.dispatchEvent(createEvent('onValidate'), valid)
    }
  })
}

VPFieldset.prototype.addField = function (field) {
  if (!(field instanceof VPField)) {
    throw new Error('[VPFieldset] Field must be an instanceof VPField')
  }
  debug('[VPFieldset] Adding field')

  this._fields.push(field)
  if (this.options.watch === true) {
    this.watchField(field)
  }
}

// TODO: Enforce onValidate structure
VPFieldset.prototype.createField = function (el, options, customRules, onValidate) {
  if (!(el instanceof Element)) {
    throw new Error('[VPFieldset] Field Element must be a valid DOMElement.')
  }

  const field = new VPField(el, options, customRules, onValidate)
  this._fields.push(field)
  if (this.options.watch === true) {
    this.watchField(field)
  }

  return field
}

VPFieldset.prototype.findFields = function () {
  const vm = this
  let fields = Array.from(this.element.getElementsByClassName(this.options.fieldClass))

  // TODO: Attribute parsing to fill in the gaps
  this._fields = fields.map(field => {
    const _field = new VPField(field, {})
    if (this.options.watch === true) {
      this.watchField(_field)
    }

    return _field
  })
}


// EventTarget
VPFieldset.prototype.listeners = null
VPFieldset.prototype.addEventListener = events.addEventListener
VPFieldset.prototype.removeEventListener = events.removeEventListener
VPFieldset.prototype.dispatchEvent = events.dispatchEvent

// DOM Messaging
VPFieldset.prototype.clearMessages = messaging.clearMessages
VPFieldset.prototype.removeMessage = messaging.removeMessage
VPFieldset.prototype.addMessage = messaging.addMessage('VPMessage')

export default VPFieldset

import VPFieldset from './Fieldset'

import debug from './util/debug'
import mergeDeep from './util/mergeDeep'
import isElemVisible from './util/isElemVisible'

import events from './lib/events'
import messaging from './lib/messaging'


const Validator = function (options, form = null) {
  if (form === null) {
    debug('[Validator] Non-strict fieldset matching.',
          'Provide a form Element or ID to enable strict matching.')
    this._form = null
    this._strict = false

    // Fieldsets being tracked by Validator
    this._fieldsets = []
  } else {
    this._form = ElementOrID(form)

    // TODO: Add strict support (can only add elements contained within the field)
    this._strict = true

    // TODO: Add child detection
    this._fieldsets = [] // GetChildFieldsets
  }

  this.listeners = {}
  // Conform to the child interfaces for messaging
  this.element = this._form
  this.options = mergeDeep({
    validateVisible: true,
    fieldsetClass: 'VPFieldset',
    errorClass: '-isError',
    messageAnchor: null,
    messagePOS: 'bottom',
    lazy: true,
    watch: false,
    scrollAnchor: null,
    scrollTo: true
  }, options)
  this.validationInputs = [
    'input', 'messagebox', 'select'
  ]
  if (Array.isArray(options.validationInputs)) {
    this._options.validationInputs = this._options.validationInputs.concat(options.valiationInputs)
  }

  // TODO: Validate options
  // TODO: Allow toggling options on whether to show messages or fire callbacks
  this._onValidation = mergeDeep({
    isValid: {
      cb: null,
      message: null
    },
    isInvalid: {
      cb: null,
      message: null
    }
  }, options.onValidation)
  this._isValid = null

  this._messageNode = null
  this._messages = []

  this._strategies = {
    'all': fields => fields.every(field => field === true),
    'some': fields => fields.some(field => field === true),
    'one': fields => fields.filter(field => field === true).length === 1
  }
}

Validator.prototype.isValid = function () {
  if (this.options.lazy) {
    this._isValid = this._fieldsets.every(fieldset => {
      if (this.options.validateVisible) {
        if(isElemVisible(fieldset.element)) {
          debug('[VPValidator] Fieldset is visible, continuing')
          return fieldset.isValid()
        } else {
          debug('[VPValidator] Skipping hidden fieldset', fieldset)
        }
      } else {
        return fieldset.isValid()
      }
    })
  } else {
    this._isValid = this._fieldsets.reduce((isValid, fieldset) => {
      if (this.options.validateVisible) {
        if(isElemVisible(fieldset.element)) {
          if (!fieldset.isValid()) {
            isValid = false
          } else {
            debug('[VPValidator] Skipping hidden fieldset', fieldset)
          }
        }
      } else {
        if (!fieldset.isValid()) {
          isValid = false
        }
      }

      return isValid
    }, true)
  }

  if (this._isValid) {
    if (this.element instanceof Element) {
      this.element.classList.remove(this.options.errorClass)
    }

    if (typeof this._onValidation.isValid.cb === 'function') {
      this._onValidation.isValid.cb()
    }
    if (typeof this._onValidation.isValid.message === 'string') {
      this.addMessage(this._onValidation.isValid.message, '-isValid')
    }
  } else {
    if (this.element instanceof Element) {
      this.element.classList.add(this.options.errorClass)
    }
    if (this.options.scrollTo === true) {
      let scrollElement
      if (this.options.scrollAnchor instanceof Element) {
        scrollElement = this.options.scrollAnchor
      } else {
        const firstElement = this._fieldsets.filter(f => f._isValid === false)[0]
        scrollElement = firstElement.element
      }

      if (typeof scrollElement.scrollIntoView === 'function') {
        scrollElement.scrollIntoView()
      } else {
        debug('[VP] Element Scrolling failed.')
      }
    }

    if (typeof this._onValidation.isInvalid.cb === 'function') {
      this._onValidation.isInvalid.cb()
    }
    if (typeof this._onValidation.isInvalid.message === 'string') {
      this.addMessage(this._onValidation.isInvalid.message, '-isValid')
    }
  }

  return this._isValid
}

// TODO: Child state checks
// TODO: Add MutationObserver on children
Validator.prototype.addFieldset = function (fieldset) {
  if (!(fieldset instanceof VPFieldset)) {
    throw new Error('[Validator] Fieldset must be an instanceof VPFieldset')
  }

  this._fieldsets.push(fieldset)
  if (this.options.watch === true) {
    this.watchFieldset(_fieldset)
  }

}

// TODO: method to remove watchers
Validator.prototype.watchFieldset = function (fieldset) {
  if (!(fieldset instanceof VPFieldset)) return

  // TODO: Optimize by tracking state and only revalidating
  // if internal state changes. Currently wasteful
  fieldset.addEventListener('onValidate', (e, isValid) => {
    const emit = this._isValid !== null
    this.isValid()
  })
}

// TODO: Remove MutationObserver on children
Validator.prototype.removeFieldset = function (fieldset) {
  const index = this._fieldsets.indexOf(fieldset)
  if (index !== -1) {
    this._fieldsets = this._fieldsets.splice(index, 1)
  }
}

// TODO: Append Predefined Fields w/ CB logic
// TODO: Validate onValidate structure
// TODO: Add MutationObserver on children
Validator.prototype.createFieldset = function (fs, strategy, options, fields, onValidate = null) {
  const fieldset = ElementOrID(fs, this._form)

  if (fieldset === null) {
    debug('[Validator] Requires a valid fieldset HTMLElement.')
    return false
  }

  const _strategy = this._strategies[strategy] || function () {
    throw new Error('[Validator] Invalid Validation Strategy')
  }
  const _fieldset = new VPFieldset(fieldset, _strategy, options, onValidate)
  fields.forEach(field => {
    _fieldset.addField(field)
  })

  this._fieldsets.push(_fieldset)
  if (this.options.watch === true) {
    this.watchFieldset(_fieldset)
  }

  return _fieldset
}

// EventTarget
Validator.prototype.listeners = null
Validator.prototype.addEventListener = events.addEventListener
Validator.prototype.removeEventListener = events.removeEventListener
Validator.prototype.dispatchEvent = events.dispatchEvent

// DOM Messaging
Validator.prototype.clearMessages = messaging.clearMessages
Validator.prototype.removeMessage = messaging.removeMessage
Validator.prototype.addMessage = messaging.addMessage('VPMessage')

// TODO: Strict enforcement
const ElementOrID = function (ElorID, form = null) {
  if (ElorID instanceof Element) return ElorID
  if (typeof ElorID === 'string') {
    let f = (form !== null && form instanceof Element)
      ? form.getElemenyById(ElorID)
      : window.document.getElementById(ElorID)

    if (f instanceof Element) return f
  }

  return null
}

export default Validator
